import styles from "./styles.module.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { ReactMediaRecorder, useMediaRecorder } from "react-media-recorder"; // Import useMediaRecorder
const Main = () => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const [isRecording, setIsRecording] = useState(false);
  const { status, startRecording, stopRecording, mediaBlobUrl } = useMediaRecorder({ video: true }); // Use useMediaRecorder hook
  const saveRecording = async () => {
    if (mediaBlobUrl) {
      const response = await fetch(mediaBlobUrl);
      const blob = await response.blob();
      const formData = new FormData();
      formData.append("recording", blob);
      await axios.post("/api/recordings", formData);
    }
  };

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>facebook</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          Logout
        </button>
      </nav>
      <div>
        {status === "ready" && <button onClick={startRecording}>Start Recording</button>}
        {status === "recording" && <button onClick={stopRecording}>Stop Recording</button>}
        {mediaBlobUrl && <button onClick={saveRecording}>Save Recording</button>}
        <ReactMediaRecorder
          video
          render={({ status, startRecording, stopRecording }) => (
            <div>
              {status === "ready" && <button onClick={startRecording}>Start Recording</button>}
              {status === "recording" && <button onClick={stopRecording}>Stop Recording</button>}
            </div>
          )}
        />
      </div>
    </div>
  );
};

export default Main;
