import React, { useState, useEffect, useRef } from "react";
import { Button } from "reactstrap";
import axios from "axios";

const ScreenRecording = () => {
  const [recording, setRecording] = useState(false);
  const [mediaChunks, setMediaChunks] = useState([]);
  const mediaRecorderRef = useRef(null);

  useEffect(() => {
    if (!mediaRecorderRef.current) {
      const constraints = {
        video: {
          mediaSource: "screen",
        },
        audio: false, // No audio
      };

      // Initialize mediaRecorderRef with MediaRecorder
      navigator.mediaDevices
        .getDisplayMedia(constraints)
        .then((stream) => {
          const mediaRecorder = new MediaRecorder(stream);
          mediaRecorderRef.current = mediaRecorder;

          mediaRecorder.ondataavailable = (event) => {
            if (event.data.size > 0) {
              setMediaChunks((prevChunks) => [...prevChunks, event.data]);
            }
          };
        })
        .catch((error) => {
          console.error("Error accessing display media:", error);
        });
    }
  }, []);

  const handleRecord = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "inactive") {
      setMediaChunks([]);
      mediaRecorderRef.current.start();
      setRecording(true);
    }
  };

  const handleStopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
      mediaRecorderRef.current.stop();
      setRecording(false);
    }
  };

  const saveRecording = () => {
    if (mediaChunks.length > 0) {
      const blob = new Blob(mediaChunks, { type: "video/webm" });
      const currentTimeStamp = new Date().getTime();
      const recordingData = {
        url: URL.createObjectURL(blob),
        timestamp: currentTimeStamp,
      };
      const url = "http://localhost:8080/api/recording"; // Replace with your backend URL

      axios.post(url, recordingData)
        .then(() => {
          console.log("Recording saved successfully");
        })
        .catch((error) => {
          console.error("An error occurred while saving recording:", error);
        });
    }
  };

  return (
    <div>
      <h1>Recording Confirmation</h1>
      <p>Are you sure you want to start recording?</p>
      {!recording && (
        <Button onClick={handleRecord}>Start Recording</Button>
      )}
      {recording && (
        <Button onClick={handleStopRecording}>Stop Recording</Button>
      )}
      {!recording && mediaChunks.length > 0 && (
        <Button onClick={saveRecording}>Save Recording</Button>
      )}
    </div>
  );
};

export default ScreenRecording;
