import React, { useState } from "react";
import { useReactMediaRecorder } from "react-media-recorder";
import axios from "axios";
import "./style.css"

const ScreenRecording = ({
  screen,
  audio,
  video,
  downloadRecordingPath,
  downloadRecordingType,
  emailToSupport,
}) => {
  const [recordingNumber, setRecordingNumber] = useState(0);
  const [status, setStatus] = useState("");
  const [mediaBlobUrl, setMediaBlobUrl] = useState("");
  const [error, setError] = useState("");

  const {
    startRecording,
    stopRecording,
    mediaBlobUrl: recordedMediaBlobUrl,
  } = useReactMediaRecorder({ screen, audio, video });

  const handleStartRecording = () => {
    startRecording();
    setStatus("recording");
  };

  const handleStopRecording = async () => {
    try {
      const currentTimeStamp = new Date().getTime();
      setRecordingNumber(currentTimeStamp);

      await stopRecording();
      setStatus("stopped");
      setMediaBlobUrl(recordedMediaBlobUrl);

      const recordingData = {
        url: recordedMediaBlobUrl,
        timestamp: currentTimeStamp,
      };
      const url = "http://localhost:8080/api/recording"; // Replace with your backend URL
      await axios.post(url, recordingData);
      console.log("Recording saved successfully");
    } catch (error) {
      setError("An error occurred while stopping recording.");
      console.error(error);
    }
  };

  const viewRecording = () => {
    window.open(mediaBlobUrl, "_blank").focus();
  };

  const downloadRecording = () => {
    const pathName = `${downloadRecordingPath}_${recordingNumber}.${downloadRecordingType}`;
    try {
      if (window.navigator && window.navigator.msSaveOrOpenBlob) {
        // for IE
        window.navigator.msSaveOrOpenBlob(mediaBlobUrl, pathName);
      } else {
        // for Chrome
        const link = document.createElement("a");
        link.href = mediaBlobUrl;
        link.download = pathName;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const mailRecording = () => {
    try {
      window.location.href = `mailto:${emailToSupport}?subject=Screen recording for an Issue number ${recordingNumber}&body=Hello%20Team,%0D%0A%0D%0A${mediaBlobUrl}`;
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="screen-record-wrapper">
      <div className="status">
        {status && status !== "stopped" && (
          <p>Screen Recording Status: {status && status.toUpperCase()}</p>
        )}
        {status && status === "recording" && (
          <span className="recording-badge">Recording...</span>
        )}
      </div>
      <div className="controls">
        {status && status !== "recording" && (
          <button
            onClick={handleStartRecording}
            className="start-button"
          >
            {mediaBlobUrl ? "Record again" : "Record your Problem"}
          </button>
        )}
        {status && status === "recording" && (
          <button
            onClick={handleStopRecording}
            className="stop-button"
          >
            Stop Recording
          </button>
        )}
        {mediaBlobUrl && status && status === "stopped" && (
          <button onClick={viewRecording} className="view-button">
            View Recording
          </button>
        )}
        {downloadRecordingType &&
          mediaBlobUrl &&
          status &&
          status === "stopped" && (
            <button
              onClick={downloadRecording}
              className="download-button"
            >
              Download Recording
            </button>
          )}
        {emailToSupport && mediaBlobUrl && status && status === "stopped" && (
          <button onClick={mailRecording} className="email-button">
            Email to Support
          </button>
        )}
      </div>
    </div>
  );
};

export default ScreenRecording;
